// acme login with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'acme login with vg',
      baselineName: 'TestAcmeLogin_VG',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('acme login with vg', async () => {
    await spec.visit(driver, 'https://afternoon-savannah-68940.herokuapp.com/#')
    await eyes.open(driver, 'Eyes Selenium SDK - ACME', 'TestAcmeLogin_VG', {
      width: 1024,
      height: 768,
    })
    const username = await spec.findElement(driver, '#username')
    await spec.type(driver, username, 'adamC')
    const password = await spec.findElement(driver, '#password')
    await spec.type(driver, password, 'MySecret123?')
    await eyes.check({region: '#username'})
    await eyes.check({region: '#password'})
    await eyes.close(undefined)
  })
})
