// check frame with css stitching classic
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check frame with css stitching classic',
      stitchMode: 'CSS',
      baselineName: 'TestCheckFrame',
      appName: 'Eyes Selenium SDK - Classic API',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check frame with css stitching classic', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(driver, undefined, 'TestCheckFrame', {
      width: 700,
      height: 460,
    })
    await eyes.checkFrame('[name="frame1"]', undefined, undefined)
    await eyes.close(undefined)
  })
})
