// check frame with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check frame with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckFrame_Fluent_Scroll',
      appName: 'Eyes Selenium SDK - Fluent API',
      branchName: 'v2',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check frame with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(driver, undefined, 'TestCheckFrame_Fluent_Scroll', {
      width: 700,
      height: 460,
    })
    await eyes.check({frames: ['[name="frame1"]']})
    await eyes.close(undefined)
  })
})
