// check hovered region by element with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check hovered region by element with scroll stitching',
      hideScrollbars: false,
      stitchMode: 'Scroll',
      baselineName: 'CheckHoveredRegionByElementWithScrollStitching',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check hovered region by element with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/StickyHeaderWithRegions'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'CheckHoveredRegionByElementWithScrollStitching',
      {width: 700, height: 460}
    )
    const input = await spec.findElement(driver, '#input')
    await spec.scrollIntoView(driver, input, undefined)
    await spec.hover(driver, input, undefined)
    await eyes.check({region: input})
    await eyes.close(undefined)
  })
})
