// check modal region by selector fully with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check modal region by selector fully with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestScrollableContentInModal_Fully',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check modal region by selector fully with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/ModalsPage/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestScrollableContentInModal_Fully',
      {width: 700, height: 460}
    )
    await spec.click(driver, '#open_scrollable_content_modal')
    await eyes.check({
      region: '#scrollable_content_modal > .modal-content',
      scrollRootElement: '#scrollable_content_modal',
      isFully: true,
    })
    await eyes.close(undefined)
  })
})
