// check region by coordinates with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check region by coordinates with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckRegionByCoordinates_Fluent_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region by coordinates with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckRegionByCoordinates_Fluent_Scroll',
      {width: 700, height: 460}
    )
    await eyes.check({region: {left: 50, top: 70, width: 90, height: 110}})
    await eyes.close(undefined)
  })
})
