// check region by native selector
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'Samsung Galaxy S8',
      app:
        'https://applitools.bintray.com/Examples/eyes-android-hello-world.apk',
    })
    eyes = testSetup.getEyes({
      displayName: 'check region by native selector',
      baselineName: 'AppiumAndroidCheckRegion',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region by native selector (@native-selectors @native @mobile @headfull)', async () => {
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'AppiumAndroidCheckRegion',
      undefined
    )
    await eyes.check({region: 'android.widget.Button'})
    await eyes.close(undefined)
  })
})
