// check region by selector in overflowed frame with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check region by selector in overflowed frame with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestCheckRegionInAVeryBigFrame',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region by selector in overflowed frame with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/WixLikeTestPage/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Special Cases',
      'TestCheckRegionInAVeryBigFrame',
      {width: 700, height: 460}
    )
    await eyes.check({region: 'img', frames: ['[name="frame1"]']})
    await eyes.close(undefined)
  })
})
