// check region by selector on ie
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'ie-11'})
    eyes = testSetup.getEyes({
      displayName: 'check region by selector on ie',
      baselineName: 'CheckRegionBySelectorOnIe',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region by selector on ie (@headfull @ie)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Classic API',
      'CheckRegionBySelectorOnIe',
      {width: 700, height: 460}
    )
    await eyes.check({region: '#overflowing-div'})
    await eyes.close(undefined)
  })
})
