// check region by selector on page with sticky header with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check region by selector on page with sticky header with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckPageWithHeader_Region_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region by selector on page with sticky header with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/PageWithHeader/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Page With Header',
      'TestCheckPageWithHeader_Region_Scroll',
      {width: 700, height: 460}
    )
    await eyes.check({region: 'div.page', isFully: false})
    await eyes.close(undefined)
  })
})
