// check region fully when body is greater and non scrollable with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check region fully when body is greater and non scrollable with scroll stitching',
      stitchMode: 'Scroll',
      baselineName:
        'TestCheckElementFullyWhenBodyIsGreaterAndNonScrollable_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region fully when body is greater and non scrollable with scroll stitching', async () => {
    await spec.visit(driver, 'https://demo.applitools.com')
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckElementFullyWhenBodyIsGreaterAndNonScrollable_Scroll',
      {width: 700, height: 460}
    )
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
