// check region in frame hidden under top bar fully with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe.skip('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check region in frame hidden under top bar fully with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestCheckElementInFrameHiddenUnderTopBar_Fully_Fluent',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region in frame hidden under top bar fully with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/PageWithFrameHiddenByBar/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckElementInFrameHiddenUnderTopBar_Fully_Fluent',
      {width: 700, height: 460}
    )
    await eyes.check({
      frames: ['[name="frame1"]'],
      region: '#div1',
      isFully: true,
    })
    await eyes.close(undefined)
  })
})
