// check region with fractional metrics by selector
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check region with fractional metrics by selector',
      baselineName: 'CheckRegionWithFractionalMetrics',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check region with fractional metrics by selector', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FractionalMetrics'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'CheckRegionWithFractionalMetrics',
      {width: 700, height: 460}
    )
    await eyes.check({region: '#target'})
    await eyes.close(undefined)
  })
})
