// check scrollable modal region by selector fully with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check scrollable modal region by selector fully with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckScrollableModal_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check scrollable modal region by selector fully with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckScrollableModal_Scroll',
      {width: 700, height: 460}
    )
    await spec.click(driver, '#centered')
    await eyes.check({
      region: '#modal-content',
      scrollRootElement: '#modal1',
      isFully: true,
    })
    await eyes.close(undefined)
  })
})
