// check window after manual scroll on safari 12
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      browser: 'safari-12',
      legacy: true,
    })
    eyes = testSetup.getEyes({
      displayName: 'check window after manual scroll on safari 12',
      baselineName: 'CheckWindowAfterManualScrollOnSafari12',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window after manual scroll on safari 12 (@webdriver @headfull @safari)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Classic API',
      'CheckWindowAfterManualScrollOnSafari12',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollBy(0, 350)', ...[])
    await eyes.check({})
    await eyes.close(undefined)
  })
})
