// check window after manual scroll with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check window after manual scroll with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckWindowAfterScroll_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window after manual scroll with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Classic API',
      'TestCheckWindowAfterScroll_Scroll',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollBy(0, 350)', ...[])
    await eyes.check({})
    await eyes.close(undefined)
  })
})
