// check window fully on page with burger menu
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check window fully on page with burger menu',
      stitchMode: 'CSS',
      baselineName: 'CheckPageWithBurgerMenuFully',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window fully on page with burger menu', async () => {
    await spec.visit(
      driver,
      'http://applitools.github.io/demo/TestPages/PageWithBurgerMenu'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'CheckPageWithBurgerMenuFully',
      {width: 700, height: 460}
    )
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
