// check window fully on page with horizontal scroll with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'firefox'})
    eyes = testSetup.getEyes({
      displayName:
        'check window fully on page with horizontal scroll with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestHorizonalScroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window fully on page with horizontal scroll with css stitching (@headfull @firefox)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/horizontal-scroll.html'
    )
    await eyes.open(driver, 'Applitools Eyes SDK', 'TestHorizonalScroll', {
      width: 600,
      height: 400,
    })
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
