// check window fully with fixed scroll root element
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'check window fully with fixed scroll root element',
      baselineName: 'TestWindowWithModal_Fully_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window fully with fixed scroll root element', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/ModalsPage/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestWindowWithModal_Fully_Scroll',
      {width: 700, height: 460}
    )
    await spec.click(driver, '#open_scrollable_modal')
    await eyes.check({scrollRootElement: '#scrollable_modal', isFully: true})
    await eyes.close(undefined)
  })
})
