// check window with layout breakpoints
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'check window with layout breakpoints',
      browsersInfo: [
        {name: 'chrome', width: 1000, height: 800},
        {iosDeviceInfo: {deviceName: 'iPad (7th generation)'}},
        {chromeEmulationInfo: {deviceName: 'Pixel 4 XL'}},
      ],
      baselineName: 'CheckWindowWithLayoutBreakpoints',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window with layout breakpoints', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/JsLayout'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'CheckWindowWithLayoutBreakpoints',
      undefined
    )
    await eyes.check({layoutBreakpoints: [500, 1000]})
    await eyes.close(undefined)
  })
})
