// should abort if not closed with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'should abort if not closed with vg',
      baselineName: 'ShouldAbortIfNotClosedWithVg',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should abort if not closed with vg', async () => {
    await spec.visit(driver, 'data:text/html,<p>Test</p>')
    await eyes.open(driver, 'Test Abort', 'ShouldAbortIfNotClosedWithVg', {
      width: 1200,
      height: 800,
    })
    await eyes.check({})
    await eyes.abort()
  })
})
