// should find regions by visual locator with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'should find regions by visual locator with vg',
      baselineName: 'TestVisualLocators_VG',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should find regions by visual locator with vg', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'TestVisualLocators_VG',
      undefined
    )
    const regionsMap = await eyes.locate({locatorNames: ['applitools_title']})
    await eyes.close(false)
    assert.deepStrictEqual(
      regionsMap,
      {applitools_title: [{left: 3, top: 19, width: 158, height: 38}]},
      undefined
    )
  })
})
