// should handle check of stale element in frame if selector is preserved
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe.skip('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should handle check of stale element in frame if selector is preserved',
      baselineName:
        'ShouldHandleCheckOfStaleElementInFrameIfSelectorIsPreserved',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should handle check of stale element in frame if selector is preserved (@webdriver)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/RefreshDomPage/iframe'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldHandleCheckOfStaleElementInFrameIfSelectorIsPreserved',
      {width: 600, height: 500}
    )
    const frameElement = await spec.findElement(driver, '[name="frame"]')
    await spec.childContext(driver, frameElement)
    const var_5 = await spec.findElement(driver, '#inner-img')
    await spec.click(driver, '#refresh-button')
    await spec.childContext(driver, null)
    await eyes.check({frames: [frameElement], region: var_5})
    await eyes.close(undefined)
  })
})
