// should not check if disabled
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should not check if disabled',
      isDisabled: true,
      baselineName: 'hello world',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should not check if disabled', async () => {
    await spec.visit(driver, 'https://applitools.com/helloworld?diff1')
    await eyes.open(driver, 'Demo C# app', 'hello world', {
      width: 800,
      height: 600,
    })
    await eyes.check({isFully: true})
    await eyes.check({region: '.random-number'})
    await eyes.check({})
    await eyes.check({region: '#someId'})
    await eyes.close(undefined)
  })
})
