// should not fail if scroll root is stale
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should not fail if scroll root is stale',
      baselineName: 'ShouldNotFailIfScrollRootIsStale',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should not fail if scroll root is stale', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/RefreshDomPage'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldNotFailIfScrollRootIsStale',
      {width: 600, height: 500}
    )
    await eyes.check({})
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/RefreshDomPage'
    )
    await eyes.check({})
    await eyes.close(undefined)
  })
})
