// should render special characters
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'should render special characters',
      browsersInfo: [{name: 'chrome', width: 800, height: 600}],
      branchName: 'default',
      baselineName: 'Special Characters',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should render special characters', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/SpecialCharacters/index.html'
    )
    await eyes.open(
      driver,
      'Special Characters Test',
      'Special Characters',
      undefined
    )
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
