// should return test results from close with failed classic test
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should return test results from close with failed classic test',
      baselineName: 'TestCloseReturnsTestResults_Failed_ClassicRunner',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should return test results from close with failed classic test', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/RandomizePage/?randomize'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'TestCloseReturnsTestResults_Failed_ClassicRunner',
      {width: 700, height: 460}
    )
    await eyes.check({region: '#random_wrapper'})
    const result = await eyes.close(false)
    assert.ok(result.constructor.name === 'TestResults', undefined)
  })
})
