// should send correct region coordinates in target region with css stitching fully
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should send correct region coordinates in target region with css stitching fully',
      baselineName: 'Test Layout Region within Target Region',
      stitchMode: 'CSS',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send correct region coordinates in target region with css stitching fully', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/PaddedBody/index.html'
    )
    await eyes.open(
      driver,
      'Test Layout Region within Target Region',
      'Test Layout Region within Target Region',
      {height: 700, width: 1100}
    )
    await eyes.check({
      isFully: true,
      region: '.main',
      layoutRegions: ['.minions'],
    })
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['layout']['0'],
      {left: 0, top: 81, width: 1084, height: 679},
      undefined
    )
  })
})
