// should send dom on edge legacy
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'edge-18'})
    eyes = testSetup.getEyes({
      displayName: 'should send dom on edge legacy',
      baselineName: 'ShouldSendDomOnEdgeLegacy',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send dom on edge legacy (@headfull @edge)', async () => {
    await spec.visit(
      driver,
      'http://applitools-dom-capture-origin-1.surge.sh/ie.html'
    )
    await eyes.open(driver, 'Eyes SDK', 'ShouldSendDomOnEdgeLegacy', {
      width: 700,
      height: 460,
    })
    await eyes.check({})
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['hasDom'],
      true,
      undefined
    )
  })
})
