// should send floating region by coordinates in frame with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should send floating region by coordinates in frame with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestCheckRegionInFrame3_Fluent',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send floating region by coordinates in frame with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckRegionInFrame3_Fluent',
      {width: 700, height: 460}
    )
    await eyes.check({
      frames: ['[name="frame1"]'],
      floatingRegions: [
        {
          region: {left: 200, top: 200, width: 150, height: 150},
          maxUpOffset: 25,
          maxDownOffset: 25,
          maxLeftOffset: 25,
          maxRightOffset: 25,
        },
      ],
      matchLevel: 'Layout',
      isFully: true,
    })
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['floating']['0'],
      {
        left: 200,
        top: 200,
        width: 150,
        height: 150,
        maxUpOffset: 25,
        maxDownOffset: 25,
        maxLeftOffset: 25,
        maxRightOffset: 25,
      },
      undefined
    )
  })
})
