// should send floating region by selector with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should send floating region by selector with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestCheckWindowWithFloatingBySelector_Fluent_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send floating region by selector with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckWindowWithFloatingBySelector_Fluent_Scroll',
      {width: 700, height: 460}
    )
    await eyes.check({
      floatingRegions: [
        {
          region: '#overflowing-div',
          maxUpOffset: 3,
          maxDownOffset: 3,
          maxLeftOffset: 20,
          maxRightOffset: 30,
        },
      ],
    })
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['floating']['0'],
      {
        left: 8,
        top: 81,
        width: 304,
        height: 184,
        maxUpOffset: 3,
        maxDownOffset: 3,
        maxLeftOffset: 20,
        maxRightOffset: 30,
      },
      undefined
    )
  })
})
