// should send ignore region by the same selector as target region with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should send ignore region by the same selector as target region with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestCheckElementWithIgnoreRegionBySameElement_Fluent',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send ignore region by the same selector as target region with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckElementWithIgnoreRegionBySameElement_Fluent',
      {width: 700, height: 460}
    )
    await eyes.check({
      region: '#overflowing-div-image',
      ignoreRegions: ['#overflowing-div-image'],
    })
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['0'],
      {left: 0, top: 0, width: 304, height: 184},
      undefined
    )
  })
})
