// should send ignore regions by selector with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'should send ignore regions by selector with vg',
      baselineName:
        'TestCheckFullWindowWithMultipleIgnoreRegionsBySelector_Fluent_VG',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send ignore regions by selector with vg', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckFullWindowWithMultipleIgnoreRegionsBySelector_Fluent_VG',
      {width: 700, height: 460}
    )
    await eyes.check({ignoreRegions: ['.ignore'], isFully: true})
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['0'],
      {left: 10, top: 286, width: 800, height: 500},
      undefined
    )
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['1'],
      {left: 122, top: 933, width: 456, height: 306},
      undefined
    )
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['2'],
      {left: 8, top: 1277, width: 690, height: 206},
      undefined
    )
  })
})
