// should set viewport size on edge legacy
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'edge-18'})
    eyes = testSetup.getEyes({
      displayName: 'should set viewport size on edge legacy',
      baselineName: 'ShouldSetViewportSizeOnEdgeLegacy',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should set viewport size on edge legacy (@headfull @edge)', async () => {
    await eyes.constructor.setViewportSize(driver, {width: 600, height: 600})
    const actualViewportSize = await spec.executeScript(
      driver,
      'return {width: window.innerWidth, height: window.innerHeight}',
      ...[]
    )
    assert.deepStrictEqual(
      actualViewportSize,
      {width: 600, height: 600},
      undefined
    )
  })
})
