// should throw if no checkpoints before close
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should throw if no checkpoints before close',
      baselineName: 'TestGetAllTestResults',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should throw if no checkpoints before close', async () => {
    await eyes.open(driver, 'Applitools Eyes SDK', 'TestGetAllTestResults', {
      width: 800,
      height: 600,
    })
    await assert.rejects(
      async () => {
        await eyes.close(undefined)
      },
      undefined,
      undefined
    )
    await eyes.getRunner().getAllTestResults(false)
  })
})
