// should throw if target frame is not found
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should throw if target frame is not found',
      baselineName: 'ShouldThrowIfTargetFrameIsNotFound',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should throw if target frame is not found', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldThrowIfTargetFrameIsNotFound',
      {width: 700, height: 460}
    )
    await assert.rejects(
      async () => {
        await eyes.check({frames: ['non-existing-frame']})
      },
      undefined,
      undefined
    )
    await eyes.abort()
  })
})
